                                            value={typeOfActivity}
                                            label="Select Activity:"
                                            onChange={handleChangeCategoryOption}
                                        >
                                            <MenuItem value={"Companion"}>Companion</MenuItem>
                                            <MenuItem value={"Tutor"}>Tutor</MenuItem>
                                            <MenuItem value={"Helper"}>Helper</MenuItem>
                                        </Select>
                                    </FormControl>
                                </Grid> : null} */}
                                </Grid>

                            </Box>
                        </div>
                    )
                }
            })

        } else {
            return null;
        }
    }

    function fabIcons() {
        if (userProfile.length == 0) {
            return null;
        } else {
            return (
                <Box sx={{ position: 'fixed', bottom: 0, left: 0, right: 0 }}>
                    {profile["Access"][0].RoleId != "Buddy" ? <Fab color="primary" className="add-service" aria-label="add" onClick={() => navigate('/services')}>
                        <AddIcon />
                    </Fab> : null}
                    <Fab color="success" className="cash-in" aria-label="cash-in" onClick={() => navigate('/cash-in')}>
                        <AccountBalanceWalletIcon />
                    </Fab>
                </Box>
            )
        }
    }

    function getWallet() {
        return (
            <Box component="form" validate onSubmit={handleSubmit} sx={{ mt: 3, mb: 2 }}>
                <h5><strong>WALLET:</strong></h5>
                <Grid container spacing={2} >
                    <Grid item xs={12}>
                        <TextField
                            autoComplete="given-name"
                            name="Firstname"
                            fullWidth
                            id="Firstname"
                            label="First Name"
                        />
                    </Grid>


                </Grid>

            </Box>
        )
    }

    if (userProfile.length == 0) {
        return (
            <Box sx={{ display: 'flex', justifyContent: "center" }}>
                <CircularProgress />
            </Box>
        );
    } else {
        return (
            <Box sx={{ pb: 7 }} ref={ref}>
                <CssBaseline />
                <ElevationScroll {...props}>
                    <AppBar style={{ background: '#087830' }}>
                        <Toolbar>
                            <Typography variant="h6" component="div">
                                Profile
                            </Typography>
                        </Toolbar>
                    </AppBar>
                </ElevationScroll>
                <Toolbar />

                {fabIcons()}
                <ThemeProvider theme={theme}>
                    <Container component="main" maxWidth="xs">
                        {getProfile()}
                        {getWallet()}
                    </Container>
                </ThemeProvider>

                <Paper sx={{ position: 'fixed', bottom: 0, left: 0, right: 0 }} className="foreground-layer" elevation={3}>
                    <BottomNavigation
                        showLabels
                        value={value}
                        onChange={(event, newValue) => {
                            setValue(newValue);
                        }}
                    >
                        {profile["Access"][0].RoleId == "Buddy" ? <BottomNavigationAction label="Services" onClick={() => navigate('/home')} icon={<RestoreIcon />} /> : null}
                        <BottomNavigationAction label="On Going" onClick={() => navigate('/on-going-activity')} icon={<FavoriteIcon />} />
                        <BottomNavigationAction label="History" onClick={() => navigate('/history')} icon={<ArchiveIcon />} />
                        {/* <BottomNavigationAction label="Profile" onClick={() => navigate('/profile')} icon={<ArchiveIcon />} /> */}
                    </BottomNavigation>
                </Paper>
            </Box>
        );
    }
}
