import React, { useEffect } from 'react';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import Cards from './cards.js';
import { useNavigate } from 'react-router-dom';
import { request } from "../../api/api";

import Fab from '@mui/material/Fab';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import AddIcon from '@mui/icons-material/Add';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';
import RestoreIcon from '@mui/icons-material/Restore';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ArchiveIcon from '@mui/icons-material/Archive';
import Paper from '@mui/material/Paper';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';

import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import useScrollTrigger from '@mui/material/useScrollTrigger';


function ElevationScroll(props) {
    const { children, window } = props;
    // Note that you normally won't need to set the window ref as useScrollTrigger
    // will default to window.
    // This is only being set here because the demo is in an iframe.
    const trigger = useScrollTrigger({
        disableHysteresis: true,
        threshold: 0,
        target: window ? window() : undefined,
    });

    return React.cloneElement(children, {
        elevation: trigger ? 4 : 0,
    });
}

ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};


ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};


export default function Profile(props) {
    const navigate = useNavigate();

    const [expanded, setExpanded] = React.useState(false);
    const [value, setValue] = React.useState(3);
    const [userProfile, setUserProfile] = React.useState([]);
    const [wallet, setWallet] = React.useState({});
    const ref = React.useRef(null);
    const [profile, setProfile] = React.useState({ Access: [{ RoleId: "" }] });

    useEffect(() => {
        let session = localStorage.getItem("userProfile");
        setProfile(JSON.parse(session));
    }, [])

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    useEffect(() => {
        async function fetchData() {
            let session = localStorage.getItem("session");

            const result = await request('getProfile', null);
            const wallet = await request('getWallet', null, { userId: session });

            setWallet(wallet);
            setUserProfile(result.data);
            // alert(JSON.stringify(result.data));
        }

        fetchData();

    }, [])


    function getProfile() {
        if (userProfile.length > 0) {
            return userProfile.map((item, i) => {
                if (item._id == localStorage.getItem("session")) {
                    return (
                        <div className='row col-md-12 m-3' key={i}>
                            <label><strong>Firstname:</strong> {item.Firstname}</label>
                            <label><strong>Lastname:</strong> {item.Lastname}</label>
                            <label><strong>Email:</strong> {item.Emailaddress}</label>
                            <label><strong>Age:</strong> {item.Age}</label>
                            <label><strong>Address:</strong> {item.Address}</label>
                            <label><strong>Gender:</strong> {item.Gender}</label>
                            <label><strong>Bank Account</strong></label>
                            <label><strong>Account Number:</strong> {item.Bankaccount[0]["Accountno"]}</label>
                            <label><strong>Account Name:</strong> {item.Bankaccount[0]["Accountname"]}</label>
                            <label><strong>Bank Name:</strong> {item.Bankaccount[0]["Bankname"]}</label>
                            <label><strong>Location:</strong> {item.Bankaccount[0]["Location"]}</label>

                            <label className='mt-3'><strong>Wallet</strong></label>
                            <label><strong>Balance:</strong> {wallet["Balance"]}</label>
                            <label><strong>Rewards:</strong> {wallet["Rewards"]}</label>
                        </div>
                    )
                }
            })

        }
    }

    return (
        <Box sx={{ pb: 7 }} ref={ref}>
            <CssBaseline />
            <ElevationScroll {...props}>
                <AppBar style={{ background: '#087830' }}>
                    <Toolbar>
                        <Typography variant="h6" component="div">
                            Profile
                        </Typography>
                    </Toolbar>
                </AppBar>
            </ElevationScroll>
            <Toolbar />

            {/* {getProfile()} */}
            <Box component="form" validate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={4}>
                        <TextField
                            autoComplete="given-name"
                            name="Firstname"
                            required
                            color={userDetails["Firstname"] == "" && validate ? "error" : ""}
                            fullWidth
                            id="Firstname"
                            label="First Name"
                            focused={validate}
                            autoFocus
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        <TextField
                            required
                            fullWidth
                            id="Middlename"
                            color={userDetails["Middlename"] == "" && validate ? "error" : ""}
                            label="M.I."
                            name="Middlename"
                            focused={validate}
                            autoComplete="family-name"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        <TextField
                            required
                            fullWidth
                            color={userDetails["Lastname"] == "" && validate ? "error" : ""}
                            id="Lastname"
                            label="Last Name"
                            name="Lastname"
                            focused={validate}
                            autoComplete="family-name"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Emailaddress"
                            color={userDetails["Emailaddress"] == "" && validate ? "error" : ""}
                            label="Email Address"
                            name="Emailaddress"
                            focused={validate}
                            autoComplete="Emailaddress"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Studentno"
                            color={userDetails["Studentno"] == "" && validate ? "error" : ""}
                            label="Student Number"
                            name="Studentno"
                            focused={validate}
                            autoComplete="Studentno"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Contactno"
                            color={userDetails["Contactno"] == "" && validate ? "error" : ""}
                            label="Mobile Number"
                            name="Contactno"
                            focused={validate}
                            autoComplete="Contactno"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Age"
                            color={userDetails["Age"] == "" && validate ? "error" : ""}
                            label="Age"
                            name="Age"
                            focused={validate}
                            autoComplete="age"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Address"
                            color={userDetails["Address"] == "" && validate ? "error" : ""}
                            label="Address"
                            name="Address"
                            focused={validate}
                            autoComplete="Address"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
                        <RadioGroup
                            aria-labelledby="demo-radio-buttons-group-label"
                            defaultValue="female"
                            name="radio-buttons-group"
                            onChange={(e, value) => {
                                let user = userDetails;
                                if (value == 'female') {
                                    user["Gender"] = "Female";
                                } else {
                                    user["Gender"] = "Male";
                                }
                                setUserDetails(user);
                            }}
                        >
                            <FormControlLabel value="female" control={<Radio />} label="Female" />
                            <FormControlLabel value="male" control={<Radio />} label="Male" />
                        </RadioGroup>
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Country"
                            label="Country"
                            color={userDetails["Country"] == "" && validate ? "error" : ""}
                            focused={validate}
                            name="Country"
                            autoComplete="Country"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Civilstatus"
                            label="Civil Status"
                            name="Civilstatus"
                            color={userDetails["Civilstatus"] == "" && validate ? "error" : ""}
                            autoComplete="Civilstatus"
                            focused={validate}
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Schoolcampus"
                            label="School Campus"
                            name="Schoolcampus"
                            color={userDetails["Schoolcampus"] == "" && validate ? "error" : ""}
                            autoComplete="Schoolcampus"
                            focused={validate}
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Degree"
                            color={userDetails["Degree"] == "" && validate ? "error" : ""}
                            label="Degree"
                            focused={validate}
                            name="Degree"
                            autoComplete="Degree"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Course"
                            label="Course"
                            name="Course"
                            color={userDetails["Course"] == "" && validate ? "error" : ""}
                            autoComplete="Course"
                            focused={validate}
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Username"
                            label="Username"
                            name="Username"
                            color={userDetails["Username"] == "" && validate ? "error" : ""}
                            focused={validate}
                            autoComplete="Username"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            name="Password"
                            label="Password"
                            type="Password"
                            id="Password"
                            color={userDetails["Password"] == "" && validate ? "error" : ""}
                            autoComplete="Password"
                            onChange={handleChange}
                        />
                    </Grid>

                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Accountno"
                            label="Account No."
                            name="Accountno"
                            color={userDetails["Accountno"] == "" && validate ? "error" : ""}
                            focused={validate}
                            autoComplete="Accountno"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Accountname"
                            label="Account Name"
                            name="Accountname"
                            color={userDetails["Accountname"] == "" && validate ? "error" : ""}
                            focused={validate}
                            autoComplete="Accountname"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Bankname"
                            label="Bank Name"
                            name="Bankname"
                            color={userDetails["Bankname"] == "" && validate ? "error" : ""}
                            focused={validate}
                            autoComplete="Bankname"
                            onChange={handleChange}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                            required
                            fullWidth
                            id="Location"
                            label="Location"
                            name="Location"
                            color={userDetails["Location"] == "" && validate ? "error" : ""}
                            focused={validate}
                            autoComplete="Location"
                            onChange={handleChange}
                        />
                    </Grid>
                    {/* <Grid item xs={12}>
                                <FormControlLabel
                                    control={<Checkbox value="allowExtraEmails" color="primary" />}
                                    label="I want to receive inspiration, marketing promotions and updates via email."
                                />
                            </Grid> */}
                    <Grid item xs={12}>
                        <FormControl fullWidth error={typeOfRole == "" && validate ? true : false}>
                            <InputLabel id="demo-simple-select-label">Type of Role:</InputLabel>
                            <Select
                                labelId="demo-simple-select-label"
                                id="role"
                                name="role"
                                color={userDetails["Lastname"] == "" && validate ? "error" : ""}
                                focused={validate}
                                value={typeOfRole}
                                label="Select Role:"
                                onChange={handleChangeOption}
                            >
                                <MenuItem value={"Student"}>Student</MenuItem>
                                <MenuItem value={"Buddy"}>Buddy</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    {typeOfRole == "Buddy" ? <Grid item xs={12}>
                        <FormControl fullWidth error={typeOfActivity == "" && validate ? true : false}>
                            <InputLabel id="demo-simple-select-label">Service Category:</InputLabel>
                            <Select
                                labelId="demo-simple-select-label"
                                id="type_of_activity"
                                name="type_of_activity"
                                value={typeOfActivity}
                                label="Select Activity:"
                                onChange={handleChangeCategoryOption}
                            >
                                <MenuItem value={"Companion"}>Companion</MenuItem>
                                <MenuItem value={"Tutor"}>Tutor</MenuItem>
                                <MenuItem value={"Helper"}>Helper</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid> : null}
                </Grid>
                <Button
                    type="submit"
                    onClick={doLogin}
                    fullWidth
                    variant="contained"
                    sx={{ mt: 3, mb: 2 }}
                >
                    Sign Up
                </Button>
                <Grid container justifyContent="flex-end">
                    <Grid item>
                        <Link href="/login" variant="body2">
                            Already have an account? Sign in
                        </Link>
                    </Grid>
                </Grid>
            </Box>

            {profile["Access"][0].RoleId != "Buddy" ? <Fab color="primary" className="add-service" aria-label="add" onClick={() => navigate('/services')}>
                <AddIcon />
            </Fab> : null}
            <Fab color="success" className="cash-in" aria-label="cash-in" onClick={() => navigate('/cash-in')}>
                <AccountBalanceWalletIcon />
            </Fab>
            <Paper sx={{ position: 'fixed', bottom: 0, left: 0, right: 0 }} elevation={3}>
                <BottomNavigation
                    showLabels
                    value={value}
                    onChange={(event, newValue) => {
                        setValue(newValue);
                    }}
                >
                    {profile["Access"][0].RoleId == "Buddy" ? <BottomNavigationAction label="Services" onClick={() => navigate('/home')} icon={<RestoreIcon />} /> : null}
                    <BottomNavigationAction label="On Going" onClick={() => navigate('/on-going-activity')} icon={<FavoriteIcon />} />
                    <BottomNavigationAction label="History" onClick={() => navigate('/history')} icon={<ArchiveIcon />} />
                    <BottomNavigationAction label="Profile" onClick={() => navigate('/profile')} icon={<ArchiveIcon />} />
                </BottomNavigation>
            </Paper>
        </Box>
    );
}
