import React, { useEffect, useState } from 'react';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Cloud from '@mui/icons-material/Cloud';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import useScrollTrigger from '@mui/material/useScrollTrigger';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

function ElevationScroll(props) {
    const { children, window } = props;
    // Note that you normally won't need to set the window ref as useScrollTrigger
    // will default to window.
    // This is only being set here because the demo is in an iframe.
    const trigger = useScrollTrigger({
        disableHysteresis: true,
        threshold: 0,
        target: window ? window() : undefined,
    });

    return React.cloneElement(children, {
        elevation: trigger ? 4 : 0,
    });
}

ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};


ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};

function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
}

const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
];

export default function Home(props) {
    const navigate = useNavigate();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [expanded, setExpanded] = React.useState(false);
    const [value, setValue] = React.useState(0);
    const ref = React.useRef(null);
    const [userProfile, setUserProfile] = React.useState({});
    const [data, setData] = useState({ hits: [] });
    const [cells, setCells] = useState(["(mm/dd/yyyy)", "Temp(°F)", "Description", "Main", "Pressure", "Humidity"]);

    useEffect(async () => {
        const url = "https://api.openweathermap.org/data/2.5/forecast/daily?lat=10.9451559&lon=124.4093897&cnt=16&appid=447de07f839b7e9041672cd99146d7e4&units=imperial";

        const fetchData = async () => {
            try {
                const response = await fetch(url);
                const json = await response.json();
                setData(json.list);
            } catch (error) {
                console.log("error", error);
            }
        };

        fetchData();
    }, []);

    return (
        <React.Fragment>
            <CssBaseline />

            <ElevationScroll {...props}>

                <AppBar style={{ background: '#87CEEB' }} >
                    <Toolbar>
                        <IconButton
                            size="large"
                            aria-label="account of current user"
                            aria-controls="menu-appbar"
                            aria-haspopup="true"
                            color="inherit"
                        >
                            <Cloud />
                        </IconButton>
                        <Typography variant="h6" component="div" sx={{ flex: 1 }}>
                            Weather Forecast
                        </Typography>
                        <div>
                            <Button
                                type="submit"
                                variant="contained"
                            >
                                Logout
                            </Button>
                        </div>

                    </Toolbar>
                </AppBar>

            </ElevationScroll>
            <Toolbar />
            <Box
                sx={{
                    my: 8,
                    mx: 4,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                }}
            >
                <TableContainer component={Paper}>
                    <Table sx={{ minWidth: 650 }} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell colSpan={6}><Typography variant="h5" gutterBottom >Date</Typography></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableHead>
                            <TableRow>
                                {cells.length > 0 && cells.map((dt, i) =>
                                (<TableCell key={i} align="center">
                                    <Typography variant="h6" gutterBottom >{dt}</Typography>
                                </TableCell>)
                                )}

                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {data.length > 0 && data.map((row, i) => (
                                <TableRow
                                    key={i}
                                    sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                >
                                    <TableCell component="th" scope="row" align="center">
                                        {new Date(1546108200 * 1000).toLocaleDateString("en-US")}
                                    </TableCell>
                                    <TableCell align="center">{row.temp.day}</TableCell>
                                    <TableCell align="center">{row.weather[0].description}</TableCell>
                                    <TableCell align="center">{row.weather[0].main}</TableCell>
                                    <TableCell align="center">{row.pressure}</TableCell>
                                    <TableCell align="center">{row.humidity}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                <Grid container sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'end',
                }}>
                    <Button
                        type="submit"
                        variant="contained"
                        sx={{ mt: 3, mb: 2, }}
                        onClick={() => navigate('/home')}
                    >
                        Back
                    </Button>
                </Grid>
            </Box>

        </React.Fragment >
    );
}
