                    <Typography variant="h6" component="div" sx={{ flex: 1 }} >
                        {user.name}
                    </Typography>
                    <Typography variant="h6" component="div" sx={{ flex: 1 }} >
                        {`https://github.com/${user.nickname}`}
                    </Typography>

                    <Stack spacing={2} sx={{ width: 300 }}>
                        <Autocomplete
                            id="free-solo-demo"
                            freeSolo
                            InputLabelProps={{ shrink: false }}
                            variant="outlined"
                            options={top100Films.map((option) => option.title)}
                            renderInput={(params) => <TextField {...params} label="City" InputProps={{
                                startAdornment: <Cloud position="start" />
                            }} />}
                        />
                    </Stack>

                    <Button
                        type="submit"
                        variant="contained"
                        sx={{ mt: 3, mb: 2 }}
                        onClick={() => navigate('/weather')}
                    >
                        Display Weather
                    </Button>
