import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FormControlLabel from '@mui/material/FormControlLabel';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import Cards from './cards.js';
import { useNavigate } from 'react-router-dom';
import { request } from "../../api/api";

import TextField from '@mui/material/TextField';
import Fab from '@mui/material/Fab';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import AddIcon from '@mui/icons-material/Add';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';
import RestoreIcon from '@mui/icons-material/Restore';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ArchiveIcon from '@mui/icons-material/Archive';
import Paper from '@mui/material/Paper';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';

import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import useScrollTrigger from '@mui/material/useScrollTrigger';

import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import FormLabel from '@mui/material/FormLabel';
import RadioGroup from '@mui/material/RadioGroup';
import Radio from '@mui/material/Radio';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Container from '@mui/material/Container';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import ImageUpload from "./imageUpload";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const theme = createTheme();
function ElevationScroll(props) {
    const { children, window } = props;
    // Note that you normally won't need to set the window ref as useScrollTrigger
    // will default to window.
    // This is only being set here because the demo is in an iframe.
    const trigger = useScrollTrigger({
        disableHysteresis: true,
        threshold: 0,
        target: window ? window() : undefined,
    });

    return React.cloneElement(children, {
        elevation: trigger ? 4 : 0,
    });
}

ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};


ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};

const finalTheme = createTheme({
    components: {
        MuiTextField: {
            variants: [ // variants will help you define the props given by Mui and the styles you want to apply for it
                {
                    props: { variant: 'outlined', disabled: true },
                    style: {
                        backgroundColor: 'green',
                        color: 'red',
                        fontSize: '5rem'
                    }
                }
            ]
        }
    }
})

export default function Profile(props) {
    const navigate = useNavigate();

    const [expanded, setExpanded] = React.useState(false);
    const [value, setValue] = React.useState(3);
    const [userProfile, setUserProfile] = React.useState([]);
    const [wallet, setWallet] = React.useState({});
    const ref = React.useRef(null);
    const [profile, setProfile] = React.useState({ Access: [{ RoleId: "" }] });
    const [typeOfRole, setTypeOfRole] = React.useState('');
    const [validate, setValidate] = React.useState(false);
    const [selectedFile, setSelectedFile] = React.useState(null);
    let [userDetails, setUserDetails] = useState({

        "Studentno": "",
        "Firstname": "",
        "Middlename": "",
        "Lastname": "",
        "Emailaddress": "",
        "Age": "",
        "Address": "",
        "Gender": "Female",
        "Country": "",
        "Contactno": "",
        "Civilstatus": "",
        "Schoolcampus": "",
        "Degree": "",
        "Course": "",
        "Pictureblob": "",
        "ApprovedBy": "",
        "Servicecategorypriority": "",
        "RoleId": "Student",
        "Username": "",
        "Password": "",
        "Accountno": "",
        "Accountname": "",
        "Bankname": "",
        "Location": ""

    });
    const [typeOfActivity, setTypeOfActivity] = React.useState('');

    const handleChangeOption = (event) => {
        setTypeOfRole(event.target.value);
    };

    const handleChangeCategoryOption = (event) => {
        setTypeOfActivity(event.target.value);
    };

    const handleChange = (e) => {
        e.preventDefault();
        userDetails[e.target.name] = e.target.value;
        setUserDetails(userDetails);
        console.log(JSON.stringify(userDetails));
    }

    async function doLogin() {
        const payload = userDetails;
        setValidate(true);
        payload["RoleId"] = typeOfRole;
        if (typeOfRole == "Buddy") {
            payload["Servicecategorypriority"] = typeOfActivity;
        }
        if (selectedFile != null) {
            payload["Pictureblob"] = selectedFile;
        }
        const result = await request('register', payload);
        if (result["data"] != null) {
            toast.success("Registered successfully!");
            navigate('/login');
        } else {
            // alert(JSON.stringify(result));
            toast.warn("Profile validation failed!");
        }

    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        console.log({
            email: data.get('email'),
            password: data.get('password'),
        });
    };


    const galleryImageList = [
        "https://raw.githubusercontent.com/dxyang/StyleTransfer/master/style_imgs/mosaic.jpg",
        "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ea/Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg/1280px-Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg",
        "https://raw.githubusercontent.com/ShafeenTejani/fast-style-transfer/master/examples/dora-maar-picasso.jpg",
        "https://pbs.twimg.com/profile_images/925531519858257920/IyYLHp-u_400x400.jpg",
        "https://raw.githubusercontent.com/ShafeenTejani/fast-style-transfer/master/examples/dog.jpg",
        "http://r.ddmcdn.com/s_f/o_1/cx_462/cy_245/cw_1349/ch_1349/w_720/APL/uploads/2015/06/caturday-shutterstock_149320799.jpg"
    ];

    useEffect(() => {
        let session = localStorage.getItem("userProfile");
        setProfile(JSON.parse(session));
    }, [])

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    useEffect(() => {
        async function fetchData() {
            let session = localStorage.getItem("session");

            const result = await request('getProfile', null);
            const wallet = await request('getWallet', null, { userId: session });

            setWallet(wallet);
            setUserProfile(result.data);
            // alert(JSON.stringify(result.data));
        }

        fetchData();

    }, [])


    function getProfile() {
        if (userProfile.length > 0) {
            return userProfile.map((item, i) => {
                if (item._id == localStorage.getItem("session")) {
                    return (
                        // <div className='row col-md-12 m-3' key={i}>
                        //     <label><strong>Firstname:</strong> {item.Firstname}</label>
                        //     <label><strong>Lastname:</strong> {item.Lastname}</label>
                        //     <label><strong>Email:</strong> {item.Emailaddress}</label>
                        //     <label><strong>Age:</strong> {item.Age}</label>
                        //     <label><strong>Address:</strong> {item.Address}</label>
                        //     <label><strong>Gender:</strong> {item.Gender}</label>
                        //     <label><strong>Bank Account</strong></label>
                        //     <label><strong>Account Number:</strong> {item.Bankaccount[0]["Accountno"]}</label>
                        //     <label><strong>Account Name:</strong> {item.Bankaccount[0]["Accountname"]}</label>
                        //     <label><strong>Bank Name:</strong> {item.Bankaccount[0]["Bankname"]}</label>
                        //     <label><strong>Location:</strong> {item.Bankaccount[0]["Location"]}</label>

                        //     <label className='mt-3'><strong>Wallet</strong></label>
                        //     <label><strong>Balance:</strong> {wallet["Balance"]}</label>
                        //     <label><strong>Rewards:</strong> {wallet["Rewards"]}</label>
                        // </div>
                        <Box component="form" validate onSubmit={handleSubmit} sx={{ mt: 3, mb: 10 }}>
                            <FormControl fullWidth error={selectedFile == null && validate ? true : false}>
                                <InputLabel id="demo-simple-select-label">{selectedFile == null ? "Select Profile:" : ""}</InputLabel>
                                <ImageUpload cardName="Input Image" imageGallery={galleryImageList} setSelectedFile={(i) => setSelectedFile(i)} selectedFile={selectedFile} />
                            </FormControl>
                            <Grid container spacing={2} >
                                <Grid item xs={12} sm={4}>
                                    <TextField
                                        autoComplete="given-name"
                                        name="Firstname"

                                        fullWidth
                                        id="Firstname"
                                        label="First Name"
                                        value={item.Firstname}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={4}>
                                    <TextField

                                        fullWidth
                                        id="Middlename"
                                        label="M.I."
                                        name="Middlename"
                                        autoComplete="family-name"
                                        value={item.Middlename}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={4}>
                                    <TextField

                                        required
                                        fullWidth
                                        value={item.Lastname}
                                        id="Lastname"
                                        label="Last Name"
                                        name="Lastname"
                                        autoComplete="family-name"
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Emailaddress"
                                        color={userDetails["Emailaddress"] == "" && validate ? "error" : ""}
                                        label="Email Address"
                                        name="Emailaddress"
                                        focused={validate}
                                        value={item.Emailaddress}
                                        autoComplete="Emailaddress"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Studentno"
                                        color={userDetails["Studentno"] == "" && validate ? "error" : ""}
                                        label="Student Number"
                                        name="Studentno"
                                        value={item.Studentno}
                                        focused={validate}
                                        autoComplete="Studentno"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Contactno"
                                        color={userDetails["Contactno"] == "" && validate ? "error" : ""}
                                        label="Mobile Number"
                                        name="Contactno"
                                        focused={validate}
                                        value={item.Contactno}
                                        autoComplete="Contactno"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Age"
                                        color={userDetails["Age"] == "" && validate ? "error" : ""}
                                        label="Age"
                                        name="Age"
                                        focused={validate}
                                        value={item.Age}
                                        autoComplete="age"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Address"
                                        color={userDetails["Address"] == "" && validate ? "error" : ""}
                                        label="Address"
                                        name="Address"
                                        value={item.Address}
                                        focused={validate}
                                        autoComplete="Address"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
                                    <RadioGroup
                                        aria-labelledby="demo-radio-buttons-group-label"
                                        defaultValue={item.Gender}
                                        name="radio-buttons-group"
                                        onChange={(e, value) => {
                                            let user = userDetails;
                                            if (value == 'Female') {
                                                user["Gender"] = "Female";
                                            } else {
                                                user["Gender"] = "Male";
                                            }
                                            setUserDetails(user);
                                        }}
                                    >
                                        <FormControlLabel value="Female" control={<Radio />} label="Female" />
                                        <FormControlLabel value="Male" control={<Radio />} label="Male" />
                                    </RadioGroup>
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Country"
                                        value={item.Country}
                                        label="Country"
                                        color={userDetails["Country"] == "" && validate ? "error" : ""}
                                        focused={validate}
                                        name="Country"
                                        autoComplete="Country"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Civilstatus"
                                        label="Civil Status"
                                        name="Civilstatus"
                                        value={item.Civilstatus}
                                        color={userDetails["Civilstatus"] == "" && validate ? "error" : ""}
                                        autoComplete="Civilstatus"
                                        focused={validate}
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Schoolcampus"
                                        value={item.Schoolcampus}
                                        label="School Campus"
                                        name="Schoolcampus"
                                        color={userDetails["Schoolcampus"] == "" && validate ? "error" : ""}
                                        autoComplete="Schoolcampus"
                                        focused={validate}
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Degree"
                                        value={item.Degree}
                                        color={userDetails["Degree"] == "" && validate ? "error" : ""}
                                        label="Degree"
                                        focused={validate}
                                        name="Degree"
                                        autoComplete="Degree"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Course"
                                        label="Course"
                                        value={item.Course}
                                        name="Course"
                                        color={userDetails["Course"] == "" && validate ? "error" : ""}
                                        autoComplete="Course"
                                        focused={validate}
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Username"
                                        value={item.Access[0].Username}
                                        label="Username"
                                    />
                                </Grid>
                                {/* <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        name="Password"
                                        label="Password"
                                        type="Password"
                                        value={item.Password}
                                        id="Password"
                                        color={userDetails["Password"] == "" && validate ? "error" : ""}
                                        autoComplete="Password"
                                        onChange={handleChange}
                                    />
                                </Grid> */}

                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Accountno"
                                        label="Account No."
                                        value={item.Bankaccount[0].Accountno}
                                        name="Accountno"
                                        color={userDetails["Accountno"] == "" && validate ? "error" : ""}
                                        focused={validate}
                                        autoComplete="Accountno"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Accountname"
                                        label="Account Name"
                                        name="Accountname"
                                        value={item.Bankaccount[0].Accountname}
                                        color={userDetails["Accountname"] == "" && validate ? "error" : ""}
                                        focused={validate}
                                        autoComplete="Accountname"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        required
                                        fullWidth
                                        id="Bankname"
                                        label="Bank Name"
                                        name="Bankname"
                                        value={item.Bankaccount[0].Bankname}
                                        color={userDetails["Bankname"] == "" && validate ? "error" : ""}
                                        focused={validate}
                                        autoComplete="Bankname"
                                        onChange={handleChange}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
