import React, { useState, useEffect } from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import FormLabel from '@mui/material/FormLabel';
import RadioGroup from '@mui/material/RadioGroup';
import Radio from '@mui/material/Radio';
import Box from '@mui/material/Box';
import { request } from "../../api/api";
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import ImageUpload from "./imageUpload";
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Copyright(props) {
    return (
        <Typography variant="body2" color="text.secondary" align="center" {...props}>
            {'Copyright Â© '}
            <Link color="inherit" href="https://mui.com/">
                Your Website
            </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const theme = createTheme();

export default function Index() {
    const navigate = useNavigate();
    const [typeOfRole, setTypeOfRole] = React.useState('');
    const [validate, setValidate] = React.useState(false);
    const [selectedFile, setSelectedFile] = React.useState(null);
    let [userDetails, setUserDetails] = useState({

        "Studentno": "",
        "Firstname": "",
        "Middlename": "",
        "Lastname": "",
        "Emailaddress": "",
        "Age": "",
        "Address": "",
        "Gender": "Female",
        "Country": "",
        "Contactno": "",
        "Civilstatus": "",
        "Schoolcampus": "",
        "Degree": "",
        "Course": "",
        "Pictureblob": "",
        "ApprovedBy": "",
        "Servicecategorypriority": "",
        "RoleId": "Student",
        "Username": "",
        "Password": "",
        "Accountno": "",
        "Accountname": "",
        "Bankname": "",
        "Location": ""

    });
    const [typeOfActivity, setTypeOfActivity] = React.useState('');

    const handleChangeOption = (event) => {
        setTypeOfRole(event.target.value);
    };

    const handleChangeCategoryOption = (event) => {
        setTypeOfActivity(event.target.value);
    };

    const handleChange = (e) => {
        e.preventDefault();
        userDetails[e.target.name] = e.target.value;
        setUserDetails(userDetails);
        console.log(JSON.stringify(userDetails));
    }

    async function doLogin() {
        const payload = userDetails;
        setValidate(true);
        payload["RoleId"] = typeOfRole;
        if (typeOfRole == "Buddy") {
            payload["Servicecategorypriority"] = typeOfActivity;
        }
        if (selectedFile != null) {
            payload["Pictureblob"] = selectedFile;
        }
        const result = await request('register', payload);
        if (result["data"] != null) {
            toast.success("Registered successfully!");
            navigate('/login');
        } else {
            // alert(JSON.stringify(result));
            toast.warn("Profile validation failed!");
        }

    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        console.log({
            email: data.get('email'),
            password: data.get('password'),
        });
    };

    const galleryImageList = [
        "https://raw.githubusercontent.com/dxyang/StyleTransfer/master/style_imgs/mosaic.jpg",
        "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ea/Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg/1280px-Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg",
        "https://raw.githubusercontent.com/ShafeenTejani/fast-style-transfer/master/examples/dora-maar-picasso.jpg",
        "https://pbs.twimg.com/profile_images/925531519858257920/IyYLHp-u_400x400.jpg",
        "https://raw.githubusercontent.com/ShafeenTejani/fast-style-transfer/master/examples/dog.jpg",
        "http://r.ddmcdn.com/s_f/o_1/cx_462/cy_245/cw_1349/ch_1349/w_720/APL/uploads/2015/06/caturday-shutterstock_149320799.jpg"
    ];

    return (
        <ThemeProvider theme={theme}>
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <Box
                    sx={{
                        marginTop: 8,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                >
                    <Avatar sx={{ m: 1, bgcolor: '#00703c' }}>
                        <LockOutlinedIcon />
                    </Avatar>
                    <Typography component="h1" variant="h5">
                        Registration
                    </Typography>
                    <FormControl fullWidth error={selectedFile == null && validate ? true : false}>
                        <InputLabel id="demo-simple-select-label">{selectedFile == null ? "Select Profile:" : ""}</InputLabel>
                        <ImageUpload cardName="Input Image" imageGallery={galleryImageList} setSelectedFile={(i) => setSelectedFile(i)} selectedFile={selectedFile} />
                    </FormControl>
                    <Box component="form" validate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                        <Grid container spacing={2}>
                            <Grid item xs={12} sm={4}>
                                <TextField
                                    autoComplete="given-name"
                                    name="Firstname"
                                    required
                                    color={userDetails["Firstname"] == "" && validate ? "error" : ""}
                                    fullWidth
                                    id="Firstname"
                                    label="First Name"
                                    focused={validate}
                                    autoFocus
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12} sm={4}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Middlename"
                                    color={userDetails["Middlename"] == "" && validate ? "error" : ""}
                                    label="M.I."
                                    name="Middlename"
                                    focused={validate}
                                    autoComplete="family-name"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12} sm={4}>
                                <TextField
                                    required
                                    fullWidth
                                    color={userDetails["Lastname"] == "" && validate ? "error" : ""}
                                    id="Lastname"
                                    label="Last Name"
                                    name="Lastname"
                                    focused={validate}
                                    autoComplete="family-name"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Emailaddress"
                                    color={userDetails["Emailaddress"] == "" && validate ? "error" : ""}
                                    label="Email Address"
                                    name="Emailaddress"
                                    focused={validate}
                                    autoComplete="Emailaddress"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Studentno"
                                    color={userDetails["Studentno"] == "" && validate ? "error" : ""}
                                    label="Student Number"
                                    name="Studentno"
                                    focused={validate}
                                    autoComplete="Studentno"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Contactno"
                                    color={userDetails["Contactno"] == "" && validate ? "error" : ""}
                                    label="Mobile Number"
                                    name="Contactno"
                                    focused={validate}
                                    autoComplete="Contactno"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Age"
                                    color={userDetails["Age"] == "" && validate ? "error" : ""}
                                    label="Age"
                                    name="Age"
                                    focused={validate}
                                    autoComplete="age"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Address"
                                    color={userDetails["Address"] == "" && validate ? "error" : ""}
                                    label="Address"
                                    name="Address"
                                    focused={validate}
                                    autoComplete="Address"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
                                <RadioGroup
                                    aria-labelledby="demo-radio-buttons-group-label"
                                    defaultValue="female"
                                    name="radio-buttons-group"
                                    onChange={(e, value) => {
                                        let user = userDetails;
                                        if (value == 'female') {
                                            user["Gender"] = "Female";
                                        } else {
                                            user["Gender"] = "Male";
                                        }
                                        setUserDetails(user);
                                    }}
                                >
                                    <FormControlLabel value="female" control={<Radio />} label="Female" />
                                    <FormControlLabel value="male" control={<Radio />} label="Male" />
                                </RadioGroup>
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Country"
                                    label="Country"
                                    color={userDetails["Country"] == "" && validate ? "error" : ""}
                                    focused={validate}
                                    name="Country"
                                    autoComplete="Country"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Civilstatus"
                                    label="Civil Status"
                                    name="Civilstatus"
                                    color={userDetails["Civilstatus"] == "" && validate ? "error" : ""}
                                    autoComplete="Civilstatus"
                                    focused={validate}
                                    onChange={handleChange}
                                />
                            </Grid>
                            {/* <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Schoolcampus"
                                    label="School Campus"
                                    name="Schoolcampus"
                                    color={userDetails["Schoolcampus"] == "" && validate ? "error" : ""}
                                    autoComplete="Schoolcampus"
                                    focused={validate}
                                    onChange={handleChange}
                                />
                            </Grid> */}
                            {schoolCampus == "Buddy" ? <Grid item xs={12}>
                                <FormControl fullWidth error={schoolCampus == "" && validate ? true : false}>
                                    <InputLabel id="demo-simple-select-label">School Campus:</InputLabel>
                                    <Select
                                        labelId="demo-simple-select-label"
                                        id="type_of_activity"
                                        name="type_of_activity"
                                        value={schoolCampus}
                                        label="Select Activity:"
                                        onChange={handleChangeCampusOption}
                                    >
                                        <MenuItem value={"Companion"}>DE LA SALLE ANDRES SORIANO MEMORIAL COLLEGE</MenuItem>
                                        <MenuItem value={"Tutor"}>DE LA SALLE ARANETA UNIVERSITY</MenuItem>
                                        <MenuItem value={"Helper"}>DE LA SALLE-COLLEGE OF SAINT BENILDE</MenuItem>
                                        <MenuItem value={"Helper"}>DE LA SALLE JOHN BOSCO COLLEGE</MenuItem>
                                        <MenuItem value={"Helper"}>DE LA SALLE MEDICAL AND HEALTH SCIENCES INSTITUTE</MenuItem>
                                        <MenuItem value={"Helper"}>DE LA SALLE LIPA</MenuItem>
                                        <MenuItem value={"Helper"}>DE LA SALLE UNIVERSITY-DASMARIÃAS</MenuItem>
                                        <MenuItem value={"Helper"}>DE LA SALLE UNIVERSITY</MenuItem>
                                    </Select>
                                </FormControl>
                            </Grid> : null}
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Degree"
                                    color={userDetails["Degree"] == "" && validate ? "error" : ""}
                                    label="Degree"
                                    focused={validate}
                                    name="Degree"
                                    autoComplete="Degree"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Course"
                                    label="Course"
                                    name="Course"
                                    color={userDetails["Course"] == "" && validate ? "error" : ""}
                                    autoComplete="Course"
                                    focused={validate}
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Username"
                                    label="Username"
                                    name="Username"
                                    color={userDetails["Username"] == "" && validate ? "error" : ""}
                                    focused={validate}
                                    autoComplete="Username"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    name="Password"
                                    label="Password"
                                    type="Password"
                                    id="Password"
                                    color={userDetails["Password"] == "" && validate ? "error" : ""}
                                    autoComplete="Password"
                                    onChange={handleChange}
                                />
                            </Grid>

                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Accountno"
                                    label="Account No."
                                    name="Accountno"
                                    color={userDetails["Accountno"] == "" && validate ? "error" : ""}
                                    focused={validate}
                                    autoComplete="Accountno"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Accountname"
                                    label="Account Name"
                                    name="Accountname"
                                    color={userDetails["Accountname"] == "" && validate ? "error" : ""}
                                    focused={validate}
                                    autoComplete="Accountname"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Bankname"
                                    label="Bank Name"
                                    name="Bankname"
                                    color={userDetails["Bankname"] == "" && validate ? "error" : ""}
                                    focused={validate}
                                    autoComplete="Bankname"
                                    onChange={handleChange}
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="Location"
                                    label="Location"
                                    name="Location"
                                    color={userDetails["Location"] == "" && validate ? "error" : ""}
                                    focused={validate}
                                    autoComplete="Location"
                                    onChange={handleChange}
                                />
                            </Grid>
                            {/* <Grid item xs={12}>
                                <FormControlLabel
                                    control={<Checkbox value="allowExtraEmails" color="primary" />}
                                    label="I want to receive inspiration, marketing promotions and updates via email."
                                />
                            </Grid> */}
                            <Grid item xs={12}>
                                <FormControl fullWidth error={typeOfRole == "" && validate ? true : false}>
                                    <InputLabel id="demo-simple-select-label">Type of Role:</InputLabel>
                                    <Select
                                        labelId="demo-simple-select-label"
                                        id="role"
                                        name="role"
                                        color={userDetails["Lastname"] == "" && validate ? "error" : ""}
                                        focused={validate}
                                        value={typeOfRole}
                                        label="Select Role:"
                                        onChange={handleChangeOption}
                                    >
                                        <MenuItem value={"Student"}>Student</MenuItem>
                                        <MenuItem value={"Buddy"}>Buddy</MenuItem>
                                    </Select>
                                </FormControl>
                            </Grid>
                            {typeOfRole == "Buddy" ? <Grid item xs={12}>
                                <FormControl fullWidth error={typeOfActivity == "" && validate ? true : false}>
                                    <InputLabel id="demo-simple-select-label">Service Category:</InputLabel>
                                    <Select
                                        labelId="demo-simple-select-label"
                                        id="type_of_activity"
                                        name="type_of_activity"
                                        value={typeOfActivity}
                                        label="Select Activity:"
                                        onChange={handleChangeCategoryOption}
                                    >
                                        <MenuItem value={"Companion"}>Companion</MenuItem>
                                        <MenuItem value={"Tutor"}>Tutor</MenuItem>
                                        <MenuItem value={"Helper"}>Helper</MenuItem>
                                    </Select>
                                </FormControl>
                            </Grid> : null}
                        </Grid>
                        <Button
                            type="submit"
                            onClick={doLogin}
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                        >
                            Sign Up
                        </Button>
                        <Grid container justifyContent="flex-end">
                            <Grid item>
                                <Link href="/login" variant="body2">
                                    Already have an account? Sign in
                                </Link>
                            </Grid>
                        </Grid>
                    </Box>
                </Box>
                <ToastContainer hideProgressBar={true} />
            </Container>
        </ThemeProvider>
    );
}
