import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
// import { styles } from '../styles/styles.js';
// import '../styles/App.css';
// import './../app-responsive.css';
import { apiModule } from '../api/apiCall';

class ForgotPassword extends Component {

  constructor(props) {
    super(props);
    this.state = {
      openList: false,
      listOfFarms: [],
      selectedFarm: '--',
      isLoading: false,
      showError: '--invisible',
      email: '',
      classes: '',
      validate: ''
    };
    this.handleChanges = this.handleChanges.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChanges(e) {
    if (e) {
      this.setState({ classes: "has-val" });
      this.setState({ validate: "" });
    } else {
      this.setState({ classes: "" });
      this.setState({ validate: "alert-validate" });
    }
    this.setState({ [e.target.name]: e.target.value });
  }

  handleValidation(email_value) {
    let email = true;
    var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    if (!email_value) {
      this.setState({
        validate: " alert-validate",
        emailMessage: "Enter valid email"
      });
      email = false;
    }

    if (!re.test(email_value.toLowerCase())) {
      this.setState({
        validate: " alert-validate",
        emailMessage: "Enter valid email"
      });
      email = false;
    }
    return email;
  }

  async handleSubmit(event) {
    event.preventDefault();
    if (this.handleValidation(this.state.email)) {
      const that = this;
      let item = { email: this.state.email };
      // let postData = jsonToURI(item);
      let data = await apiModule('authentication_forgot_password', {}, item);
      if (data.status == 'success') {
        window.confirm(data.message);
        that.props.history.push("/");
      } else {
        this.setState({ showError: '--visible' });
      }
    } else {
      window.confirm("Please enter an email.")
    }
  }

  render() {
    return (
      <div className="limiter" style={styles.templateSkin}>
        <div className="login-form">
          <div className="login-form-wrapper p-t-85 p-b-20">
            <div className="login100-form validate-form">
              <span className="login100-form-title login mb-4">
                {/* <img className="login100-form login100-form__logo login--reset" alt="Phoenix Fuels logo" src={require("../assets/images/delivery-system.png")} /> */}
              </span>
              <form onSubmit={this.handleSubmit}>
                <span className="reset-form-title p-b-70">Please input email address to reset</span>
                <div className={"wrap-input100 validate-input m-b-50 " + this.state.validate} data-validate="Enter valid email">
                  <input value={this.state.email} onChange={event => { this.handleChanges(event); }} className={"input100 " + this.state.classes} type="text" name="email" />
                  <span className="focus-input100" data-placeholder="Email address"></span>
                </div>
                <div className="login-form-form-btn">
                  <button type="submit" className="login100-form-btn">SUBMIT</button>
                </div>
                <label className="regular-label login-label regular__link mt-4">Already a member? <u onClick={() => this.props.history.goBack()}>Login</u></label>
                <div className={"error-information alert-danger alert-dismissible " + this.state.showError}>
                  You have requested too much reset requests. Please try again later.
                </div>
              </form>
            </div>
          </div>
          <footer className="pt-5">
            <div className="login-form-footer">
              <a target="_blank" href="https://www.phoenixfuels.ph/contact-us" className="login-form-footer__text pr-1">Contact |</a>
              <a target="_blank" href="https://www.phoenixfuels.ph" className="login-form-footer__text">Phoenix Petroleum Philippines, Inc. | Copyright 2019</a>
            </div>
          </footer>
        </div>
        <figure className="login-image">
          <img className="login-image__background" alt="Login background" src={require("../assets/images/bg-login.png")} />
        </figure>
      </div>
    );
  }
}


export default ForgotPassword;
