import React, {useEffect} from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import FormLabel from '@mui/material/FormLabel';
import RadioGroup from '@mui/material/RadioGroup';
import Radio from '@mui/material/Radio';
import Box from '@mui/material/Box';
import { useNavigate } from 'react-router-dom';
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';
import RestoreIcon from '@mui/icons-material/Restore';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ArchiveIcon from '@mui/icons-material/Archive';
import Paper from '@mui/material/Paper';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import useScrollTrigger from '@mui/material/useScrollTrigger';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { request } from "../../api/api";
import { myGlobal } from "../../commons/global";

function ElevationScroll(props) {
    const { children, window } = props;
    // Note that you normally won't need to set the window ref as useScrollTrigger
    // will default to window.
    // This is only being set here because the demo is in an iframe.
    const trigger = useScrollTrigger({
        disableHysteresis: true,
        threshold: 0,
        target: window ? window() : undefined,
    });

    return React.cloneElement(children, {
        elevation: trigger ? 4 : 0,
    });
}

ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};


ElevationScroll.propTypes = {
    children: PropTypes.element.isRequired,
    /**
     * Injected by the documentation to work in an iframe.
     * You won't need it on your project.
     */
    window: PropTypes.func,
};

function Copyright(props) {
    return (
        <Typography variant="body2" color="text.secondary" align="center" {...props}>
            {'Copyright © '}
            <Link color="inherit" href="https://mui.com/">
                Your Website
            </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const theme = createTheme();

export default function Index(props) {
    const [value, setValue] = React.useState(new Date('2014-08-18T21:11:54'));
    const [startDateTime, setStartDateTime] = React.useState(new Date('2014-08-18T21:11:54'));
    const [endDateTime, setEndDateTime] = React.useState(new Date('2014-08-18T21:11:54'));
    const [typeOfActivity, setTypeOfActivity] = React.useState('');
    const navigate = useNavigate();

    const handleChangeOption = (event) => {
        setTypeOfActivity(event.target.value);
    };
    const handleChange = (newValue) => {
        setValue(newValue);
    };
    const handleChangeEnd = (newValue) => {
        setEndDateTime(newValue);
    };
    const handleChangeStart = (newValue) => {
        setStartDateTime(newValue);
    };
    const handleSubmit = async (event) => {
        event.preventDefault();
        const data = new FormData(event.currentTarget);
        console.log({
            title: data.get('title'),
            description: data.get('description'),
            offered_payment: data.get('offered_payment'),
            type_of_activity: data.get('type_of_activity'),
            start_date_time: startDateTime.toString(),
            endDateTime: endDateTime.toString()
        });

        const payload = {
            UserId: localStorage.getItem("session"),
            // Servicecategory: data.get('title'),
            Description: data.get('description'),
            Price: data.get('offered_payment'),
            Servicecategory: data.get('type_of_activity'),
            StartDateTime: startDateTime.toString(),
            EndDateTime: endDateTime.toString(),
            Acceptedby: 'any',
            Status: 'any',
            Cancelreasons: 'any'
        };

        const result = await request('createService', payload);


        // if(result["data"] != null){
        //     navigate('/home');
        // } else {
            alert(JSON.stringify(result));
        // }
    };

    useEffect(() => {
        // alert(JSON.stringify(localStorage.getItem("session")));
    })

    function getList () {
        return(
            <ThemeProvider theme={theme}>
            <CssBaseline />
            <ElevationScroll {...props}>
                <AppBar style={{ background: '#087830' }}>
                    <Toolbar>
                        <Typography variant="h6" component="div">
                            Create a Service
                        </Typography>
                    </Toolbar>
                </AppBar>
            </ElevationScroll>
            <Toolbar />
            <Container component="main" maxWidth="xs">
                <Box
                    sx={{
                        marginTop: 8,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                >
                    <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                        <Grid container spacing={2}>

                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="title"
                                    label="Title"
                                    name="title"
                                    autoComplete="title"
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="description"
                                    label="Description"
                                    name="description"
                                    autoComplete="description"
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    required
                                    fullWidth
                                    id="offered_payment"
                                    label="Offered Payment"
                                    name="offered_payment"
                                    autoComplete="offered_payment"
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <FormControl fullWidth>
                                    <InputLabel id="demo-simple-select-label">Type of Activity:</InputLabel>
                                    <Select
                                        labelId="demo-simple-select-label"
                                        id="type_of_activity"
                                        name="type_of_activity"
                                        value={typeOfActivity}
                                        label="Select Activity:"
                                        onChange={handleChangeOption}
                                    >
                                        <MenuItem value={"Companion"}>Companion</MenuItem>
                                        <MenuItem value={"Tutor"}>Tutor</MenuItem>
                                        <MenuItem value={"Helper"}>Helper</MenuItem>
                                    </Select>
                                </FormControl>
                            </Grid>
                            <Grid item xs={12}>
                                <LocalizationProvider dateAdapter={AdapterDateFns}>

                                    <DateTimePicker
                                        label="Start Date and Time"
                                        id="start_date_time"
                                        name="start_date_time"
                                        value={startDateTime}
                                        onChange={handleChangeStart}
                                        renderInput={(params) => <TextField {...params} />}
                                    />
                                </LocalizationProvider>
                            </Grid>
                            <Grid item xs={12}>
                                <LocalizationProvider dateAdapter={AdapterDateFns}>

                                <DateTimePicker

                                        label="End Date and Time"
                                        id="end_date_time"
                                        name="end_date_time"
                                        value={endDateTime}
                                        onChange={handleChangeEnd}
                                        renderInput={(params) => <TextField {...params} />}
                                    />
                                </LocalizationProvider>
                            </Grid>
                        </Grid>

                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                        >
                            Submit
                        </Button>
                    </Box>
                </Box>
                <Paper sx={{ position: 'fixed', bottom: 0, left: 0, right: 0 }} elevation={3}>
                    <BottomNavigation
                        showLabels
                        value={value}
                        onChange={(event, newValue) => {
                            setValue(newValue);
                        }}
                    >
                        <BottomNavigationAction label="Services" onClick={() => navigate('/home')} icon={<RestoreIcon />} />
                        <BottomNavigationAction label="On Going" onClick={() => navigate('/on-going-activity')} icon={<FavoriteIcon />} />
                        <BottomNavigationAction label="History" onClick={() => navigate('/history')} icon={<ArchiveIcon />} />
                        <BottomNavigationAction label="Profile" onClick={() => navigate('/profile')} icon={<ArchiveIcon />} />
                    </BottomNavigation>
                </Paper>
            </Container>
        </ThemeProvider>
        )
    }

    return (
        <section class="col-md-6 mt-3 mb-3">

        {getList()}

        </section>
    );
}
